export function formatCurrency(amount: number): string {
  if (amount >= 1000000) {
    return `€${(amount / 1000000).toFixed(amount % 1000000 === 0 ? 0 : 1)}M`;
  }
  if (amount >= 1000) {
    return `€${(amount / 1000).toFixed(amount % 1000 === 0 ? 0 : 1)}K`;
  }
  return `€${amount}`;
}

export function getPlayersByPosition(
  position: string,
  excludeTeam?: string,
): Player[] {
  const allPlayers: Player[] = [];
  Object.values(teams).forEach((team) => {
    if (excludeTeam && team.id === excludeTeam) return;
    allPlayers.push(...team.players.filter((p) => p.position === position));
  });
  return allPlayers.sort((a, b) => b.value - a.value);
}

export function searchPlayersByName(query: string): Player[] {
  const allPlayers: Player[] = [];
  Object.values(teams).forEach((team) => {
    allPlayers.push(...team.players);
  });
  return allPlayers
    .filter((player) => player.name.toLowerCase().includes(query.toLowerCase()))
    .sort((a, b) => b.value - a.value);
}

// Función para asignar automáticamente los avatares a todos los jugadores
function assignAvatarsToPlayers() {
  let assignedCount = 0;
  Object.values(teams).forEach((team) => {
    team.players.forEach((player) => {
      if (!player.avatarUrl) {
        const avatarUrl = getPlayerAvatar(player.id);
        if (avatarUrl) {
          player.avatarUrl = avatarUrl;
          assignedCount++;
        }
      }
    });
  });
  console.log(`🖼️ Avatares asignados automáticamente: ${assignedCount} jugadores`);
}

